import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 59.1-207.44 Enforcement; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-207.44.md","filePath":"vacode/59.1-207.44.md"}'),o={name:"vacode/59.1-207.44.md"},n=r('<h1 id="§-59-1-207-44-enforcement-penalties" tabindex="-1">§ 59.1-207.44 Enforcement; penalties. <a class="header-anchor" href="#§-59-1-207-44-enforcement-penalties" aria-label="Permalink to &quot;§ 59.1-207.44 Enforcement; penalties.&quot;">​</a></h1><p>Any violation of this chapter shall constitute a prohibited practice under the provisions of § <a href="http://law.lis.virginia.gov/vacode/59.1-200/" target="_blank" rel="noreferrer">59.1-200</a> and shall be subject to the enforcement provisions of Chapter 17 (§ <a href="http://law.lis.virginia.gov/vacode/59.1-196/" target="_blank" rel="noreferrer">59.1-196</a> et seq.). It shall be the responsibility of any supplier who uses a comparison price to be able to substantiate the basis for any price comparisons made by the supplier. Upon the request of the Attorney General, any attorney for the Commonwealth, or the attorney of any county, city, or town, a supplier shall provide documentation to substantiate the basis for any comparison price utilized by the supplier in any advertisement governed by this chapter. No provision of this chapter shall be construed to apply to any supplier whose advertising practices are governed by § <a href="http://law.lis.virginia.gov/vacode/46.2-1581/" target="_blank" rel="noreferrer">46.2-1581</a>.</p><p>1992, c. 768; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',3),i=[n];function s(l,p,c,h,f,_){return t(),a("div",null,i)}const b=e(o,[["render",s]]);export{v as __pageData,b as default};
