import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 59.1-21.16:2 Operation of retail outlet by refiner; apportionment of fuels during periods of shortage; rules and regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-21.16:2.md","filePath":"vacode/59.1-21.16:2.md"}'),a={name:"vacode/59.1-21.16:2.md"},n=t('<h1 id="§-59-1-21-16-2-operation-of-retail-outlet-by-refiner-apportionment-of-fuels-during-periods-of-shortage-rules-and-regulations" tabindex="-1">§ 59.1-21.16:2 Operation of retail outlet by refiner; apportionment of fuels during periods of shortage; rules and regulations. <a class="header-anchor" href="#§-59-1-21-16-2-operation-of-retail-outlet-by-refiner-apportionment-of-fuels-during-periods-of-shortage-rules-and-regulations" aria-label="Permalink to &quot;§ 59.1-21.16:2 Operation of retail outlet by refiner; apportionment of fuels during periods of shortage; rules and regulations.&quot;">​</a></h1><p>A. After July 1, 1979, no refiner of petroleum products shall operate any major brand, secondary brand, or unbranded retail outlet in the Commonwealth of Virginia with company personnel, a parent company, or under a contract with any person, firm, or corporation, managing a service station on a fee arrangement with the refiner; however, such refiner may operate such retail outlet with the aforesaid personnel, parent, person, firm, or corporation if such outlet is located not less than one and one-half miles from the nearest retail outlet operated by any dealer or jobber/distributor, as measured by the most direct surface transportation route from the gas pump at the refiner&#39;s facility that is nearest a gas pump at the dealer&#39;s or jobber/distributor&#39;s facility; and provided, that once in operation, no refiner shall be required to change or cease operation of any retail outlet by the provisions of this section.</p><p>During the period July 1, 1990, through June 30, 1991, no refiner may construct and operate with company personnel as defined in this section any new major brand, secondary brand, or unbranded retail outlet in the Commonwealth of Virginia, except on any property purchased or under option to purchase by March 1, 1990.</p><p>B. Every refiner of petroleum products shall apportion all gasoline and diesel fuel among their purchasers during periods of shortages on an equitable basis.</p><p>C. No new lease, lease renewal, new supply contract, or new supply contract renewal under this chapter shall impose purchase or sales quotas.</p><p>D. The provisions of this section shall not be applicable to retail outlets operated by producers or refiners on July 1, 1979.</p><p>1979, c. 306; 1984, c. 720; 1990, c. 907; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0664" target="_blank" rel="noreferrer">664</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0351" target="_blank" rel="noreferrer">351</a>.</p>',7),i=[n];function s(l,p,u,c,d,f){return r(),o("div",null,i)}const m=e(a,[["render",s]]);export{g as __pageData,m as default};
