import{_ as i,o as t,c as o,z as e,a as n}from"./chunks/framework.9482e208.js";const x=JSON.parse('{"title":"§ 59.1-258 Blind bidding prohibited.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-258.md","filePath":"vacode/59.1-258.md"}'),r={name:"vacode/59.1-258.md"},a=e("h1",{id:"§-59-1-258-blind-bidding-prohibited",tabindex:"-1"},[n("§ 59.1-258 Blind bidding prohibited. "),e("a",{class:"header-anchor",href:"#§-59-1-258-blind-bidding-prohibited","aria-label":'Permalink to "§ 59.1-258 Blind bidding prohibited."'},"​")],-1),h=e("p",null,"A. Blind bidding is hereby prohibited within the Commonwealth. No bids shall be returnable, no negotiations for the exhibition or licensing of a motion picture shall take place, and no license agreement or any of its terms shall be agreed to, for the exhibition of any motion picture within the Commonwealth before the motion picture has either been trade screened within the Commonwealth or the District of Columbia or Prince Georges and Montgomery Counties, Maryland, or before such motion picture, at the option of the distributor, otherwise has been made available for viewing within the Commonwealth or the District of Columbia or Prince Georges and Montgomery Counties, Maryland, by all exhibitors from whom the distributor is soliciting bids or with whom the distributor is negotiating for the right to exhibit the motion picture.",-1),d=e("p",null,"B. A distributor shall provide reasonable and uniform notice of the trade screening of any motion picture to those exhibitors within the Commonwealth from whom he intends to solicit bids or with whom he intends to negotiate for the right to exhibit that motion picture.",-1),s=e("p",null,"C. Any purported waiver of the prohibition against blind bidding in this chapter shall be void and unenforceable.",-1),l=e("p",null,"1978, c. 764.",-1),c=[a,h,d,s,l];function b(m,p,f,u,g,_){return t(),o("div",null,c)}const v=i(r,[["render",b]]);export{x as __pageData,v as default};
