import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 59.1-296.2 Contracts sold on prepayment basis.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-296.2.md","filePath":"vacode/59.1-296.2.md"}'),o={name:"vacode/59.1-296.2.md"},n=i('<h1 id="§-59-1-296-2-contracts-sold-on-prepayment-basis" tabindex="-1">§ 59.1-296.2 Contracts sold on prepayment basis. <a class="header-anchor" href="#§-59-1-296-2-contracts-sold-on-prepayment-basis" aria-label="Permalink to &quot;§ 59.1-296.2 Contracts sold on prepayment basis.&quot;">​</a></h1><p>A. Each health club selling contracts or health club services on a prepayment basis shall notify the Commissioner of the proposed facility for which prepayments will be solicited and shall deposit all funds received from such prepayment contracts in an account established in a financial institution authorized to transact business in the Commonwealth until the health club has commenced operations in the facility and the facility has remained open for a period of 30 days. The account shall be established and maintained only in a financial institution that agrees in writing with the Commissioner to hold all funds deposited and not to release such funds until receipt of written authorization from the Commissioner. The prepayment funds deposited will be eligible for withdrawal by the health club after the facility has been open and providing services pursuant to its health club contracts for 30 days and the Commissioner gives written authorization for withdrawal.</p><p>B. The provisions of this section shall not apply to any facility duly registered pursuant to the provisions of § <a href="http://law.lis.virginia.gov/vacode/59.1-296.1/" target="_blank" rel="noreferrer">59.1-296.1</a> for which a bond or letter of credit in the amount of $100,000 has been posted.</p><p>1985, c. 585; 1990, cc. 392, 433; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0439" target="_blank" rel="noreferrer">439</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0459" target="_blank" rel="noreferrer">459</a>.</p>',4),s=[n];function r(l,c,h,d,p,f){return t(),a("div",null,s)}const _=e(o,[["render",r]]);export{u as __pageData,_ as default};
