import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 59.1-297.1 Payment and calculation of refunds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-297.1.md","filePath":"vacode/59.1-297.1.md"}'),i={name:"vacode/59.1-297.1.md"},o=n('<h1 id="§-59-1-297-1-payment-and-calculation-of-refunds" tabindex="-1">§ 59.1-297.1 Payment and calculation of refunds. <a class="header-anchor" href="#§-59-1-297-1-payment-and-calculation-of-refunds" aria-label="Permalink to &quot;§ 59.1-297.1 Payment and calculation of refunds.&quot;">​</a></h1><p>A. All refunds for cancellation of membership shall be paid within 30 days of the health club&#39;s receipt of written notice of cancellation by the buyer and calculated by:</p><ol><li><p>Dividing the contract price by the term of the contract in days;</p></li><li><p>Multiplying the number obtained in subdivision 1 by the number of days between the effective date of the contract and the date of cancellation; and</p></li><li><p>Subtracting the number obtained in subdivision 2 from the total price paid on the health club contract.</p></li></ol><p>B. In the event of the health club going out of business, the date of cancellation shall be the date the health club ceased providing health club services at the facility.</p><p>C. A health club issuing a refund to a buyer under this chapter shall do so within 30 days of the health club receiving a notice of cancellation pursuant to § <a href="http://law.lis.virginia.gov/vacode/59.1-297/" target="_blank" rel="noreferrer">59.1-297</a>, or within 30 days of the permanent closing of the facility designated in the buyer&#39;s contract.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0344" target="_blank" rel="noreferrer">344</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0439" target="_blank" rel="noreferrer">439</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0459" target="_blank" rel="noreferrer">459</a>.</p>',6),r=[o];function l(c,h,s,d,f,u){return t(),a("div",null,r)}const _=e(i,[["render",l]]);export{b as __pageData,_ as default};
