import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 59.1-310.9 Requirements for accredited septic system inspectors and performance of septic system inspections.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-310.9.md","filePath":"vacode/59.1-310.9.md"}'),n={name:"vacode/59.1-310.9.md"},s=a('<h1 id="§-59-1-310-9-requirements-for-accredited-septic-system-inspectors-and-performance-of-septic-system-inspections" tabindex="-1">§ 59.1-310.9 Requirements for accredited septic system inspectors and performance of septic system inspections. <a class="header-anchor" href="#§-59-1-310-9-requirements-for-accredited-septic-system-inspectors-and-performance-of-septic-system-inspections" aria-label="Permalink to &quot;§ 59.1-310.9 Requirements for accredited septic system inspectors and performance of septic system inspections.&quot;">​</a></h1><p>A. In order to use the title of &quot;accredited septic system inspector&quot; in connection with any real estate transaction, including refinancings, an applicant shall be accredited by the National Sanitation Foundation or an equivalent national accrediting organization, which accreditation shall include the passage of both a written and practical examination on the principles and practice of septic system inspections.</p><p>In addition, the applicant shall satisfy the following requirements:</p><ol><li><p>Hold a high school diploma or equivalent; and</p></li><li><p>Have evidence of at least one year of active field experience conducting onsite septic systems inspections or completion of a nationally approved training course.</p></li></ol><p>B. Any individual who holds a valid onsite sewage system operator, onsite sewage system installer, or onsite soil evaluator license pursuant to Chapter 23 (§ <a href="/vacode/54.1-2300/">54.1-2300</a> et seq.) of Title 54.1 shall be authorized to perform a septic system inspection in connection with any real estate transaction, including refinancings.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0052" target="_blank" rel="noreferrer">52</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0521" target="_blank" rel="noreferrer">521</a>.</p>',6),o=[s];function r(c,p,l,d,f,m){return t(),i("div",null,o)}const u=e(n,[["render",r]]);export{_ as __pageData,u as default};
