import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 59.1-320.1 Review of registration application.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-320.1.md","filePath":"vacode/59.1-320.1.md"}'),o={name:"vacode/59.1-320.1.md"},r=i('<h1 id="§-59-1-320-1-review-of-registration-application" tabindex="-1">§ 59.1-320.1 Review of registration application. <a class="header-anchor" href="#§-59-1-320-1-review-of-registration-application" aria-label="Permalink to &quot;§ 59.1-320.1 Review of registration application.&quot;">​</a></h1><p>A. Once the Commissioner receives an application for registration in proper form, accompanied by the proper fee, he shall, within a reasonable period of time not to exceed forty-five days after the receipt of such application:</p><ol><li>Register the applicant if the applicant:</li></ol><p>a. Has met the requirements of § <a href="http://law.lis.virginia.gov/vacode/59.1-318/" target="_blank" rel="noreferrer">59.1-318</a>;</p><p>b. Is not in violation of § <a href="http://law.lis.virginia.gov/vacode/59.1-323/" target="_blank" rel="noreferrer">59.1-323</a>; and</p><p>c. Has a reasonable ability to discharge the obligations imposed upon him by his membership camping contract; or</p><ol start="2"><li>Issue a notice of opportunity for a hearing to consider whether the application should be denied, if it reasonably appears that the applicant fails to satisfy any provision of § <a href="http://law.lis.virginia.gov/vacode/59.1-323/" target="_blank" rel="noreferrer">59.1-323</a> or any other requirement of this chapter.</li></ol><p>B. Except as otherwise provided by order of the Commissioner, each registration shall be valid for the period from July 1 of one year until its expiration on June 30 of the following year.</p><p>1992, c. 545.</p>',9),n=[r];function p(s,l,c,h,f,d){return a(),t("div",null,n)}const v=e(o,[["render",p]]);export{m as __pageData,v as default};
