import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 59.1-335.5 Prohibited practices.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-335.5.md","filePath":"vacode/59.1-335.5.md"}'),i={name:"vacode/59.1-335.5.md"},a=o('<h1 id="§-59-1-335-5-prohibited-practices" tabindex="-1">§ 59.1-335.5 Prohibited practices. <a class="header-anchor" href="#§-59-1-335-5-prohibited-practices" aria-label="Permalink to &quot;§ 59.1-335.5 Prohibited practices.&quot;">​</a></h1><p>A credit services business, and its salespersons, agents and representatives, and independent contractors who sell or attempt to sell the services of a credit services business, shall not do any of the following:</p><ol><li><p>Charge or receive any money or other valuable consideration prior to full and complete performance of the services that the credit services business has agreed to perform for or on behalf of the consumer, unless the consumer has agreed to pay for such services during the term of a written subscription agreement that provides for the consumer to make periodic payments during the agreement&#39;s term in consideration for the credit services business&#39;s ongoing performance of services for or on behalf of the consumer, provided that such subscription agreement may be cancelled at any time by the consumer;</p></li><li><p>Charge or receive any money or other valuable consideration solely for referral of the consumer to a retail seller or to any other credit grantor who will or may extend to the consumer, if the credit that is or will be extended to the consumer is upon substantially the same terms as those available to the general public;</p></li><li><p>Make, or counsel or advise any consumer to make, any statement that is untrue or misleading and which is known, or which by the exercise of reasonable care should be known, to be untrue or misleading, to a consumer reporting agency or to any person who has extended credit to a consumer or to whom a consumer is applying for an extension of credit, with respect to a consumer&#39;s creditworthiness, credit standing, or credit capacity;</p></li><li><p>Make or use any untrue or misleading representations in the offer or sale of the services of a credit services business or engage, directly or indirectly, in any act, practice, or course of business which operates or would operate as a fraud or deception upon any person in connection with the offer or sale of the services of a credit services business; or</p></li><li><p>Advertise, offer, sell, provide, or perform any of the services of a credit services business in connection with an extension of credit that meets any of the following conditions:</p></li></ol><p>a. The amount of credit is less than $5,000;</p><p>b. The repayment term is one year or less;</p><p>c. The credit is provided under an open-end credit plan; or</p><p>d. The annual percentage rate exceeds 36 percent. For purposes of this section, &quot;annual percentage rate&quot; has the same meaning as in the federal Truth in Lending Act (15 U.S.C. § 1601 et seq.) and its implementing regulations, as they may be amended from time to time.</p><p>1989, cc. 651, 655; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0421" target="_blank" rel="noreferrer">421</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1215" target="_blank" rel="noreferrer">1215</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1258" target="_blank" rel="noreferrer">1258</a>.</p>',8),n=[a];function s(c,l,h,d,p,f){return r(),t("div",null,n)}const g=e(i,[["render",s]]);export{m as __pageData,g as default};
