import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 59.1-352.10 Failure to repurchase; civil remedy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-352.10.md","filePath":"vacode/59.1-352.10.md"}'),o={name:"vacode/59.1-352.10.md"},i=r('<h1 id="§-59-1-352-10-failure-to-repurchase-civil-remedy" tabindex="-1">§ 59.1-352.10 Failure to repurchase; civil remedy. <a class="header-anchor" href="#§-59-1-352-10-failure-to-repurchase-civil-remedy" aria-label="Permalink to &quot;§ 59.1-352.10 Failure to repurchase; civil remedy.&quot;">​</a></h1><p>A. If a supplier fails or refuses to repurchase any inventory covered under the provisions of this chapter within the time periods established in § <a href="http://law.lis.virginia.gov/vacode/59.1-352.5/" target="_blank" rel="noreferrer">59.1-352.5</a>, the supplier shall be civilly liable for one hundred percent of the current net price of the inventory, any freight charges paid by the dealer, the dealer&#39;s reasonable attorney&#39;s fee and court costs, and interest on the current net price of the inventory computed at the legal rate of interest from the ninety-first day after termination of the agreement.</p><p>B. Notwithstanding any agreement to the contrary, and in addition to any other legal remedies available, any person who suffers monetary loss due to a violation of this chapter or because he refuses to accede to a proposal for an arrangement that, if consummated, is in violation of this chapter, may bring a civil action to enjoin further violations and to recover damages sustained by him together with the costs of the suit, including a reasonable attorney&#39;s fee.</p><p>C. The provisions of §§ <a href="http://law.lis.virginia.gov/vacode/59.1-352.3/" target="_blank" rel="noreferrer">59.1-352.3</a> through <a href="http://law.lis.virginia.gov/vacode/59.1-352.8/" target="_blank" rel="noreferrer">59.1-352.8</a> shall not be waivable in any contract or agreement, and any such attempted waiver shall be null and void.</p><p>D. A civil action commenced under the provisions of this chapter shall be brought within four years after the violation complained of is or reasonably should have been discovered, whichever occurs first.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0898" target="_blank" rel="noreferrer">898</a>.</p>',6),n=[i];function s(l,h,c,d,p,f){return a(),t("div",null,n)}const m=e(o,[["render",s]]);export{v as __pageData,m as default};
