import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 59.1-352.9 Prohibited acts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-352.9.md","filePath":"vacode/59.1-352.9.md"}'),o={name:"vacode/59.1-352.9.md"},i=r('<h1 id="§-59-1-352-9-prohibited-acts" tabindex="-1">§ 59.1-352.9 Prohibited acts. <a class="header-anchor" href="#§-59-1-352-9-prohibited-acts" aria-label="Permalink to &quot;§ 59.1-352.9 Prohibited acts.&quot;">​</a></h1><p>No supplier shall do any of the following:</p><ol><li><p>Coerce any dealer to accept delivery of equipment, parts, or accessories that the dealer has not ordered voluntarily, except as required by any applicable law, or unless the parts or accessories are safety parts or accessories required by the supplier.</p></li><li><p>Condition the sale of additional equipment to a dealer upon a requirement that the dealer also purchase other goods or services, except that a supplier may require the dealer to purchase those parts reasonably necessary to maintain the quality of operation in the field of the equipment used in the trade area.</p></li><li><p>Coerce a dealer into refusing to purchase equipment manufactured by another supplier.</p></li><li><p>Terminate, cancel, or fail to renew or substantially change the competitive circumstances of the retail agreement based on the results of any circumstance beyond the dealer&#39;s control, including a natural disaster such as a sustained drought, high unemployment in the dealership market area, or a labor dispute.</p></li></ol><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0898" target="_blank" rel="noreferrer">898</a>.</p>',4),s=[i];function n(l,c,p,d,h,u){return a(),t("div",null,s)}const f=e(o,[["render",n]]);export{m as __pageData,f as default};
