import{_ as r,o as t,c as s,z as e,a as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 59.1-372 Virginia Breeders Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-372.md","filePath":"vacode/59.1-372.md"}'),n={name:"vacode/59.1-372.md"},a=e("h1",{id:"§-59-1-372-virginia-breeders-fund",tabindex:"-1"},[i("§ 59.1-372 Virginia Breeders Fund. "),e("a",{class:"header-anchor",href:"#§-59-1-372-virginia-breeders-fund","aria-label":'Permalink to "§ 59.1-372 Virginia Breeders Fund."'},"​")],-1),o=e("p",null,"There is hereby created within the State Treasury the Virginia Breeders Fund, which Fund, together with the interest thereon, shall be administered in whole or in part by the Commission or by an entity designated by the Commission. The cost of administering and promoting the Fund shall be deducted from the Fund, and the balance shall be disbursed by the Commission or designated entity to the breeders of Virginia-bred horses that win races at race meetings designated by the Commission, to the owners of Virginia sires of Virginia-bred horses that win races at race meetings designated by the Commission, to the owners of Virginia-bred horses that win or earn purse money in nonrestricted races at racetracks in Virginia licensed by the Commission, to the owners of Virginia-bred horses that win races at race meetings designated by the Commission and for purses for races restricted to Virginia-bred or Virginia-sired horses or both at race meetings designated by the Commission. To assist it in establishing this awards and incentive program to foster the industry of breeding racehorses in Virginia, the Commission shall appoint an advisory committee composed of two members from each of the registered breed associations representing each breed of horse participating in the Fund program, one member representing the owners and operators of racetracks and one member representing all of the meets sanctioned by the National Steeplechase Association.",-1),d=e("p",null,[i("1988, c. 855; 1993, c. 146; 1997, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0798",target:"_blank",rel:"noreferrer"},"798"),i("; 2002, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0852",target:"_blank",rel:"noreferrer"},"852"),i(".")],-1),h=[a,o,d];function c(m,g,l,b,f,p){return t(),s("div",null,h)}const y=r(n,[["render",c]]);export{_ as __pageData,y as default};
