import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 59.1-403 Prohibition on persons under 21 years of age; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-403.md","filePath":"vacode/59.1-403.md"}'),n={name:"vacode/59.1-403.md"},i=o('<h1 id="§-59-1-403-prohibition-on-persons-under-21-years-of-age-penalty" tabindex="-1">§ 59.1-403 Prohibition on persons under 21 years of age; penalty. <a class="header-anchor" href="#§-59-1-403-prohibition-on-persons-under-21-years-of-age-penalty" aria-label="Permalink to &quot;§ 59.1-403 Prohibition on persons under 21 years of age; penalty.&quot;">​</a></h1><p>No person shall wager on or conduct any wagering on the outcome of a horse race pursuant to the provisions of this chapter unless such person is 18 years of age or older. No person shall accept any wager from a minor. No person shall be admitted into a satellite facility if such person is under 18 years of age unless accompanied by one of his parents or his legal guardian. No person under 21 years of age shall use any electronic gaming terminal or other electronic device in a satellite facility to wager on or conduct any wagering on historical horse racing. Violation of this section shall be a Class 1 misdemeanor.</p><p>1988, c. 855; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0915" target="_blank" rel="noreferrer">915</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1025" target="_blank" rel="noreferrer">1025</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>.</p>',3),t=[i];function s(l,c,p,h,g,f){return a(),r("div",null,t)}const u=e(n,[["render",s]]);export{_ as __pageData,u as default};
