import{_ as e,o,c as n,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 59.1-41.6 Penalties for violation of chapter.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-41.6.md","filePath":"vacode/59.1-41.6.md"}'),i={name:"vacode/59.1-41.6.md"},r=t('<h1 id="§-59-1-41-6-penalties-for-violation-of-chapter" tabindex="-1">§ 59.1-41.6 Penalties for violation of chapter. <a class="header-anchor" href="#§-59-1-41-6-penalties-for-violation-of-chapter" aria-label="Permalink to &quot;§ 59.1-41.6 Penalties for violation of chapter.&quot;">​</a></h1><p>Violations of this chapter are punishable as follows:</p><ol><li><p>Except as otherwise provided in this section, any person convicted of an offense under this chapter is guilty of a Class 1 misdemeanor.</p></li><li><p>Any person convicted of an offense involving at least 100 unlawful sound recordings or twenty unlawful audio visual recordings during any 180-day period is guilty of a felony punishable by a term of imprisonment of not less than one nor more than two years, or in the discretion of the jury or the court trying the case without a jury, confinement in jail for not more than twelve months and a fine of not more than $5,000, either or both;</p></li><li><p>Any person convicted of an offense involving at least 1,000 unlawful sound recordings or 65 unlawful audio visual recordings during any 180-day period is guilty of a felony punishable by a term of imprisonment of not less than one nor more than three years, or in the discretion of the jury or the court trying the case without a jury, confinement in jail for not more than twelve months and a fine of not more than $100,000, either or both; and</p></li><li><p>Any second or subsequent felony offense under this chapter shall be punishable by a term of imprisonment of not less than one nor more than three years, or in the discretion of the jury or the court trying the case without a jury, confinement in jail for not more than twelve months and a fine of not more than $100,000, either or both.</p></li><li><p>Upon conviction of a person of any offense under this chapter, the court in its judgment of conviction may order the forfeiture and destruction or other disposition of all infringing recordings and of all implements, devices and equipment used by the person in the manufacture of the infringing recordings.</p></li></ol><p>1972, c. 618; 1989, c. 240; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),a=[r];function s(l,f,h,c,d,p){return o(),n("div",null,a)}const y=e(i,[["render",s]]);export{m as __pageData,y as default};
