import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 59.1-411 Notification.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-411.md","filePath":"vacode/59.1-411.md"}'),a={name:"vacode/59.1-411.md"},r=i('<h1 id="§-59-1-411-notification" tabindex="-1">§ 59.1-411 Notification. <a class="header-anchor" href="#§-59-1-411-notification" aria-label="Permalink to &quot;§ 59.1-411 Notification.&quot;">​</a></h1><p>A. Every notice served by any person on an owner or operator of an overhead high voltage line pursuant to § <a href="http://law.lis.virginia.gov/vacode/59.1-410/" target="_blank" rel="noreferrer">59.1-410</a> shall contain the following information:</p><ol><li><p>The name of the individual serving such notice;</p></li><li><p>The location or address of the tract or parcel of land upon which the work is to take place with sufficient particularity to enable the owner or operator of the overhead high voltage lines to ascertain the precise tract or parcel of land involved;</p></li><li><p>The name, address and work day telephone number of the person responsible for the work;</p></li><li><p>The field telephone number at the site of such work, if one is available;</p></li><li><p>The type and extent of the proposed work;</p></li><li><p>The name of the person for whom the proposed work is being performed;</p></li><li><p>The time and date of the notice; and</p></li><li><p>The dates upon which the work is scheduled to commence and be completed.</p></li></ol><p>B. If the notification required by this chapter is made by telephone, a record of such notification shall be maintained by the owner or operator notified and the person giving the notice to document compliance with the requirements of this chapter.</p><p>C. To facilitate notification required by this chapter, every operator of overhead high voltage lines shall publish a phone number or numbers that, when called, will serve to provide initial notification of the need to arrange for the temporary safety arrangements pursuant to this chapter.</p><p>D. If, after the arrangements required by § <a href="http://law.lis.virginia.gov/vacode/59.1-410/" target="_blank" rel="noreferrer">59.1-410</a> are made, a delay in commencing the work is encountered, then the person responsible for the work shall be required to give a new notice as specified in this section.</p><p>E. The provisions of this section shall not apply to the owner or leaseholder of real estate devoted to agricultural or silvicultural activities beneath a high voltage line, unless otherwise required by state or federal law.</p><p>1989, c. 341; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0298" target="_blank" rel="noreferrer">298</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0267" target="_blank" rel="noreferrer">267</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0364" target="_blank" rel="noreferrer">364</a>.</p>',8),n=[r];function l(h,s,p,c,d,f){return t(),o("div",null,n)}const v=e(a,[["render",l]]);export{u as __pageData,v as default};
