import{_ as e,o as i,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 59.1-420 Conditions for handling charges and shipping charges.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-420.md","filePath":"vacode/59.1-420.md"}'),t={name:"vacode/59.1-420.md"},n=a('<h1 id="§-59-1-420-conditions-for-handling-charges-and-shipping-charges" tabindex="-1">§ 59.1-420 Conditions for handling charges and shipping charges. <a class="header-anchor" href="#§-59-1-420-conditions-for-handling-charges-and-shipping-charges" aria-label="Permalink to &quot;§ 59.1-420 Conditions for handling charges and shipping charges.&quot;">​</a></h1><p>A. It shall be unlawful to notify a person that he will receive a gift, prize or item of value and that as a condition of receiving the gift, prize or item of value he will be required to pay any money, or purchase or lease (including rent) any goods or services, if any one or more of the following conditions exist:</p><ol><li>The shipping charge exceeds:</li></ol><p>a. The cost of postage or the charge of a delivery service in the business of delivering goods of like size, weight, and kind for shipping the gift, prize or item of value from the geographic area in which the gift, prize or item of value is being distributed; or</p><p>b. The exact amount for shipping paid to an independent fulfillment house or an independent supplier, either of which is in the business of shipping goods for shippers other than the offeror of the gift, prize or item of value; or</p><ol start="2"><li>The handling charge exceeds the lesser of five dollars or the actual cost of handling.</li></ol><p>B. This section shall apply to all offers of prizes, gifts or items of value covered by this chapter.</p><p>1989, c. 689.</p>',8),r=[n];function s(h,l,c,p,d,f){return i(),o("div",null,r)}const u=e(t,[["render",s]]);export{_ as __pageData,u as default};
