import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 59.1-442 Sale of purchaser information; notice required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-442.md","filePath":"vacode/59.1-442.md"}'),o={name:"vacode/59.1-442.md"},n=a('<h1 id="§-59-1-442-sale-of-purchaser-information-notice-required" tabindex="-1">§ 59.1-442 Sale of purchaser information; notice required. <a class="header-anchor" href="#§-59-1-442-sale-of-purchaser-information-notice-required" aria-label="Permalink to &quot;§ 59.1-442 Sale of purchaser information; notice required.&quot;">​</a></h1><p>A. No merchant, without giving notice to the purchaser, shall sell to any third person information that concerns the purchaser and that is gathered in connection with the sale, rental, or exchange of tangible personal property to the purchaser at the merchant&#39;s place of business. Notice required by this section may be by the posting of a sign or any other reasonable method. If requested by a purchaser not to sell such information, the merchant shall not do so. No merchant shall sell any information gathered solely as the result of any customer payment by personal check, credit card, or where the merchant records the number of the customer&#39;s driver&#39;s license or other document issued under Chapter 3 (§ <a href="/vacode/46.2-300/">46.2-300</a> et seq.) of Title 46.2 or the comparable law of another jurisdiction. This subsection shall not be construed as authorizing a merchant to sell to a third person any information concerning a purchaser if the sale or dissemination of the information is prohibited pursuant to § <a href="/vacode/59.1-443.3/">59.1-443.3</a>.</p><p>B. For the purposes of this section and § <a href="/vacode/59.1-443.3/">59.1-443.3</a>, &quot;merchant&quot; means any person or entity engaged in the sale of goods from a fixed retail location in Virginia.</p><p>1992, c. 807; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0789" target="_blank" rel="noreferrer">789</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0795" target="_blank" rel="noreferrer">795</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1227" target="_blank" rel="noreferrer">1227</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1246" target="_blank" rel="noreferrer">1246</a>.</p>',4),i=[n];function s(c,h,l,f,d,p){return r(),t("div",null,i)}const g=e(o,[["render",s]]);export{m as __pageData,g as default};
