import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 59.1-443.1 Recording date of birth as condition of accepting checks prohibited.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-443.1.md","filePath":"vacode/59.1-443.1.md"}'),a={name:"vacode/59.1-443.1.md"},r=i('<h1 id="§-59-1-443-1-recording-date-of-birth-as-condition-of-accepting-checks-prohibited" tabindex="-1">§ 59.1-443.1 Recording date of birth as condition of accepting checks prohibited. <a class="header-anchor" href="#§-59-1-443-1-recording-date-of-birth-as-condition-of-accepting-checks-prohibited" aria-label="Permalink to &quot;§ 59.1-443.1 Recording date of birth as condition of accepting checks prohibited.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Check&quot; shall have the same meaning as defined in § <a href="http://law.lis.virginia.gov/vacode/8.3A-104/" target="_blank" rel="noreferrer">8.3A-104</a>.</p><p>B. Except as provided in subsection C, no person who accepts checks for the transaction of business shall, as a condition of accepting the check, record, or request or require a person to record, his or her date of birth upon the check or otherwise.</p><p>C. This section does not require a person to accept checks for the transaction of business. Nothing in this section shall apply to (i) the collection or use of a date of birth that is unrelated to accepting payment by check or (ii) a requirement that the person paying by check provide the year of his birth.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0241" target="_blank" rel="noreferrer">241</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0839" target="_blank" rel="noreferrer">839</a>.</p>',6),c=[r];function n(s,h,d,p,f,l){return t(),o("div",null,c)}const b=e(a,[["render",n]]);export{g as __pageData,b as default};
