import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 59.1-444 Damages.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-444.md","filePath":"vacode/59.1-444.md"}'),o={name:"vacode/59.1-444.md"},i=r('<h1 id="§-59-1-444-damages" tabindex="-1">§ 59.1-444 Damages. <a class="header-anchor" href="#§-59-1-444-damages" aria-label="Permalink to &quot;§ 59.1-444 Damages.&quot;">​</a></h1><p>A person aggrieved by a violation of any provision of this chapter, except § <a href="http://law.lis.virginia.gov/vacode/59.1-443.2/" target="_blank" rel="noreferrer">59.1-443.2</a>, shall be entitled to institute an action to recover damages in the amount of $100 per violation. In addition, if the aggrieved party prevails, he may be awarded reasonable attorney&#39;s fees and court costs. Actions under this section shall be brought in the general district court for the city or county in which the transaction or other violation that gave rise to the action occurred. A violation of the provisions of § <a href="http://law.lis.virginia.gov/vacode/59.1-443.2/" target="_blank" rel="noreferrer">59.1-443.2</a> is a prohibited practice under the Virginia Consumer Protection Act (§ <a href="http://law.lis.virginia.gov/vacode/59.1-196/" target="_blank" rel="noreferrer">59.1-196</a> et seq.).</p><p>1992, c. 807; 1993, c. 453; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0241" target="_blank" rel="noreferrer">241</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0640" target="_blank" rel="noreferrer">640</a>.</p>',3),n=[i];function s(c,l,h,d,g,_){return a(),t("div",null,n)}const v=e(o,[["render",s]]);export{f as __pageData,v as default};
