import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 59.1-466.4 Penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-466.4.md","filePath":"vacode/59.1-466.4.md"}'),o={name:"vacode/59.1-466.4.md"},n=r('<h1 id="§-59-1-466-4-penalty" tabindex="-1">§ 59.1-466.4 Penalty. <a class="header-anchor" href="#§-59-1-466-4-penalty" aria-label="Permalink to &quot;§ 59.1-466.4 Penalty.&quot;">​</a></h1><p>Any performer or performing group, or its agent, who violates § <a href="http://law.lis.virginia.gov/vacode/59.1-466.2/" target="_blank" rel="noreferrer">59.1-466.2</a> shall be liable to the Commonwealth for a civil penalty of not less than $5,000 nor more than $15,000 per violation, which civil penalty shall be in addition to any other relief that may be granted under § <a href="http://law.lis.virginia.gov/vacode/59.1-466.3/" target="_blank" rel="noreferrer">59.1-466.3</a>. The civil penalty collected pursuant to this section shall be payable to the State Treasurer for deposit to the general fund. Each performance or production declared unlawful by § <a href="http://law.lis.virginia.gov/vacode/59.1-466.2/" target="_blank" rel="noreferrer">59.1-466.2</a> shall constitute a separate violation. Nothing in this section shall be construed as affecting any private cause of action that may exist under Virginia law.</p><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0261" target="_blank" rel="noreferrer">261</a>.</p>',3),i=[n];function l(s,c,h,p,d,_){return a(),t("div",null,i)}const v=e(o,[["render",l]]);export{g as __pageData,v as default};
