import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 59.1-466.7 Enforcement; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-466.7.md","filePath":"vacode/59.1-466.7.md"}'),r={name:"vacode/59.1-466.7.md"},o=n('<h1 id="§-59-1-466-7-enforcement-penalties" tabindex="-1">§ 59.1-466.7 Enforcement; penalties. <a class="header-anchor" href="#§-59-1-466-7-enforcement-penalties" aria-label="Permalink to &quot;§ 59.1-466.7 Enforcement; penalties.&quot;">​</a></h1><p>A. The Attorney General may cause an action to be brought in the name of the Commonwealth to enjoin any violation of § <a href="http://law.lis.virginia.gov/vacode/59.1-466.6/" target="_blank" rel="noreferrer">59.1-466.6</a> by any person and to recover a civil penalty in the amount of not less than $1,000 nor more than $5,000 for each such violation. Civil penalties paid pursuant to this section shall be deposited to the Literary Fund.</p><p>B. In an action brought under this section, the Attorney General may recover damages and such other relief allowed by law, including restitution on behalf of consumers injured by violations of § <a href="http://law.lis.virginia.gov/vacode/59.1-466.6/" target="_blank" rel="noreferrer">59.1-466.6</a>.</p><p>C. In an action brought under this section, the Attorney General may recover reasonable expenses incurred in investigating and preparing the case, and attorneys&#39; fees.</p><p>D. Whenever the Attorney General has reasonable cause to believe that any person has engaged in, is engaging in, or is about to engage in, any violation of § <a href="http://law.lis.virginia.gov/vacode/59.1-466.6/" target="_blank" rel="noreferrer">59.1-466.6</a>, the Attorney General is empowered to issue a civil investigative demand. The provisions of § <a href="http://law.lis.virginia.gov/vacode/59.1-9.10/" target="_blank" rel="noreferrer">59.1-9.10</a> shall apply, mutatis mutandis, to civil investigative demands issued pursuant to this section.</p><p>E. Nothing in this section shall be construed as affecting any private cause of action that may exist under any law of the Commonwealth.</p><p>2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0261" target="_blank" rel="noreferrer">261</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0268" target="_blank" rel="noreferrer">268</a>.</p>',7),i=[o];function s(l,c,h,p,d,f){return a(),t("div",null,i)}const u=e(r,[["render",s]]);export{v as __pageData,u as default};
