import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 59.1-481 Scope.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-481.md","filePath":"vacode/59.1-481.md"}'),i={name:"vacode/59.1-481.md"},o=r('<h1 id="§-59-1-481-scope" tabindex="-1">§ 59.1-481 Scope. <a class="header-anchor" href="#§-59-1-481-scope" aria-label="Permalink to &quot;§ 59.1-481 Scope.&quot;">​</a></h1><p>(a) Except as otherwise provided in subsection (b), this chapter applies to electronic records and electronic signatures relating to a transaction.</p><p>(b) This chapter does not apply to a transaction to the extent it is governed by:</p><p>(1) A law governing the creation and execution of wills, codicils, or testamentary trusts; and</p><p>(2) Title 8.1A except § <a href="http://law.lis.virginia.gov/vacode/8.1A-306/" target="_blank" rel="noreferrer">8.1A-306</a>, Title 8.3A, Title 8.4, Title 8.4A, Title 8.5A, Title 8.7, Title 8.8A, Title 8.9A, Title 8.10, and Title 8.11.</p><p>(c) This chapter applies to an electronic record or electronic signature otherwise excluded from the application of this chapter under subsection (b) to the extent it is governed by law other than those specified in subsection (b).</p><p>(d) A transaction subject to this chapter is also subject to other applicable substantive law.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0995" target="_blank" rel="noreferrer">995</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0086" target="_blank" rel="noreferrer">86</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0353" target="_blank" rel="noreferrer">353</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0369" target="_blank" rel="noreferrer">369</a>.</p>',8),n=[o];function c(s,l,p,h,d,_){return t(),a("div",null,n)}const b=e(i,[["render",c]]);export{f as __pageData,b as default};
