import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 59.1-501.15 Variation by agreement; commercial practice.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-501.15.md","filePath":"vacode/59.1-501.15.md"}'),i={name:"vacode/59.1-501.15.md"},n=t('<h1 id="§-59-1-501-15-variation-by-agreement-commercial-practice" tabindex="-1">§ 59.1-501.15 Variation by agreement; commercial practice. <a class="header-anchor" href="#§-59-1-501-15-variation-by-agreement-commercial-practice" aria-label="Permalink to &quot;§ 59.1-501.15 Variation by agreement; commercial practice.&quot;">​</a></h1><p>(a) The effect of any provision of this chapter, including an allocation of risk or imposition of a burden, may be varied by agreement of the parties.</p><p>(b) The following rules are not variable by agreement:</p><p>(1) Obligations of good faith, diligence, reasonableness, and care imposed by this chapter may not be disclaimed by agreement, but the parties by agreement may determine the standards by which the performance of the obligation is to be measured if the standards are not manifestly unreasonable;</p><p>(2) The limitations on enforceability imposed by unconscionability under § <a href="http://law.lis.virginia.gov/vacode/59.1-501.11/" target="_blank" rel="noreferrer">59.1-501.11</a> and fundamental public policy under § <a href="http://law.lis.virginia.gov/vacode/59.1-501.5/" target="_blank" rel="noreferrer">59.1-501.5</a> (b) may not be varied by agreement; and</p><p>(3) Limitations on enforceability of, or agreement to, a contract, term, or right expressly stated in the sections listed in the following subsections may not be varied by agreement except to the extent provided in each section:</p><p>(A) the limitations on agreed choice of law in § <a href="http://law.lis.virginia.gov/vacode/59.1-501.9/" target="_blank" rel="noreferrer">59.1-501.9</a> (a);</p><p>(B) the limitations on agreed choice of forum in § <a href="http://law.lis.virginia.gov/vacode/59.1-501.10/" target="_blank" rel="noreferrer">59.1-501.10</a>;</p><p>(C) the requirements for manifesting assent in § <a href="http://law.lis.virginia.gov/vacode/59.1-501.12/" target="_blank" rel="noreferrer">59.1-501.12</a> and opportunity for review in § <a href="http://law.lis.virginia.gov/vacode/59.1-501.13:1/" target="_blank" rel="noreferrer">59.1-501.13:1</a>;</p><p>(D) the limitations on enforceability in § <a href="http://law.lis.virginia.gov/vacode/59.1-502.1/" target="_blank" rel="noreferrer">59.1-502.1</a>;</p><p>(E) the limitations on a mass-market license in § <a href="http://law.lis.virginia.gov/vacode/59.1-502.9/" target="_blank" rel="noreferrer">59.1-502.9</a>;</p><p>(F) the consumer defense arising from an electronic error in § <a href="http://law.lis.virginia.gov/vacode/59.1-502.14/" target="_blank" rel="noreferrer">59.1-502.14</a>;</p><p>(G) the requirements for an enforceable term in §§ <a href="http://law.lis.virginia.gov/vacode/59.1-503.3/" target="_blank" rel="noreferrer">59.1-503.3</a> (b), <a href="http://law.lis.virginia.gov/vacode/59.1-503.7/" target="_blank" rel="noreferrer">59.1-503.7</a> (f), <a href="http://law.lis.virginia.gov/vacode/59.1-504.6/" target="_blank" rel="noreferrer">59.1-504.6</a> (b) and (c), and <a href="http://law.lis.virginia.gov/vacode/59.1-508.4/" target="_blank" rel="noreferrer">59.1-508.4</a> (a);</p><p>(H) the requirements of § <a href="http://law.lis.virginia.gov/vacode/59.1-503.4/" target="_blank" rel="noreferrer">59.1-503.4</a> (b) (2);</p><p>(I) the limitations on a financier in §§ <a href="http://law.lis.virginia.gov/vacode/59.1-505.7/" target="_blank" rel="noreferrer">59.1-505.7</a> through <a href="http://law.lis.virginia.gov/vacode/59.1-505.11/" target="_blank" rel="noreferrer">59.1-505.11</a>;</p><p>(J) the restrictions on altering the period of limitations in § <a href="http://law.lis.virginia.gov/vacode/59.1-508.5/" target="_blank" rel="noreferrer">59.1-508.5</a> (a) and (b); and</p><p>(K) the limitations on self-help repossession in §§ <a href="http://law.lis.virginia.gov/vacode/59.1-508.15/" target="_blank" rel="noreferrer">59.1-508.15</a> (b) and <a href="http://law.lis.virginia.gov/vacode/59.1-508.16/" target="_blank" rel="noreferrer">59.1-508.16</a>.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',18),o=[n];function l(s,c,p,g,h,f){return a(),r("div",null,o)}const m=e(i,[["render",l]]);export{v as __pageData,m as default};
