import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 59.1-501.6 Rule of construction.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-501.6.md","filePath":"vacode/59.1-501.6.md"}'),r={name:"vacode/59.1-501.6.md"},n=o('<h1 id="§-59-1-501-6-rule-of-construction" tabindex="-1">§ 59.1-501.6 Rule of construction. <a class="header-anchor" href="#§-59-1-501-6-rule-of-construction" aria-label="Permalink to &quot;§ 59.1-501.6 Rule of construction.&quot;">​</a></h1><p>(a) This chapter must be liberally construed and applied to promote its underlying purposes and policies to:</p><p>(1) support and facilitate the realization of the full potential of computer information transactions;</p><p>(2) clarify the law governing computer information transactions;</p><p>(3) enable expanding commercial practice in computer information transactions by commercial usage and agreement of the parties;</p><p>(4) promote uniformity of the law with respect to the subject matter of this chapter among States that enact it; and</p><p>(5) permit the continued expansion of commercial practices in the excluded transactions through custom, usage and agreement of the parties.</p><p>(b) Except as otherwise provided in § <a href="http://law.lis.virginia.gov/vacode/59.1-501.15/" target="_blank" rel="noreferrer">59.1-501.15</a>, the use of mandatory language or the absence of a phrase such as &quot;unless otherwise agreed&quot; in a provision of this chapter does not preclude the parties from varying the effect of the provision by agreement.</p><p>(c) The fact that a provision of this chapter imposes a condition for a result does not by itself mean that the absence of that condition yields a different result.</p><p>(d) To be enforceable, a term need not be conspicuous, negotiated, or expressly assented or agreed to, unless this chapter expressly so requires.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',11),i=[n];function s(c,p,l,h,f,u){return t(),a("div",null,i)}const g=e(r,[["render",s]]);export{m as __pageData,g as default};
