import{_ as e,o as t,c as o,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 59.1-501.7 Legal recognition of electronic record and authentication; use of electronic agents.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-501.7.md","filePath":"vacode/59.1-501.7.md"}'),a={name:"vacode/59.1-501.7.md"},r=n('<h1 id="§-59-1-501-7-legal-recognition-of-electronic-record-and-authentication-use-of-electronic-agents" tabindex="-1">§ 59.1-501.7 Legal recognition of electronic record and authentication; use of electronic agents. <a class="header-anchor" href="#§-59-1-501-7-legal-recognition-of-electronic-record-and-authentication-use-of-electronic-agents" aria-label="Permalink to &quot;§ 59.1-501.7 Legal recognition of electronic record and authentication; use of electronic agents.&quot;">​</a></h1><p>(a) A record or authentication may not be denied legal effect or enforceability solely because it is in electronic form.</p><p>(b) This chapter does not require that a record or authentication be generated, stored, sent, received, or otherwise processed by electronic means or in electronic form.</p><p>(c) In any transaction, a person may establish requirements regarding the type of authentication or record acceptable to that person.</p><p>(d) A person using an electronic agent that he has selected for making an authentication, performance, or agreement, including manifestation of assent, is bound by the operations of the electronic agent, even if no individual was aware of or reviewed the agent&#39;s operations or the results of the operations.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>.</p>',6),i=[r];function c(s,l,d,f,h,g){return t(),o("div",null,i)}const u=e(a,[["render",c]]);export{_ as __pageData,u as default};
