import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 59.1-501.9 Choice of law.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-501.9.md","filePath":"vacode/59.1-501.9.md"}'),o={name:"vacode/59.1-501.9.md"},i=r('<h1 id="§-59-1-501-9-choice-of-law" tabindex="-1">§ 59.1-501.9 Choice of law. <a class="header-anchor" href="#§-59-1-501-9-choice-of-law" aria-label="Permalink to &quot;§ 59.1-501.9 Choice of law.&quot;">​</a></h1><p>(a) The parties in their agreement may choose the applicable law. However, the choice is not enforceable in a consumer contract to the extent it would vary a statute, administrative rule, or regulation that may not be varied by agreement under the law of Virginia.</p><p>(b) In the absence of an enforceable agreement on choice of law, the contract is governed by the law of Virginia.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0762" target="_blank" rel="noreferrer">762</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0763" target="_blank" rel="noreferrer">763</a>.</p>',4),n=[i];function c(l,h,s,_,f,g){return a(),t("div",null,n)}const b=e(o,[["render",c]]);export{d as __pageData,b as default};
