import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 59.1-502.10 Terms of contract formed by conduct.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-502.10.md","filePath":"vacode/59.1-502.10.md"}'),a={name:"vacode/59.1-502.10.md"},c=o('<h1 id="§-59-1-502-10-terms-of-contract-formed-by-conduct" tabindex="-1">§ 59.1-502.10 Terms of contract formed by conduct. <a class="header-anchor" href="#§-59-1-502-10-terms-of-contract-formed-by-conduct" aria-label="Permalink to &quot;§ 59.1-502.10 Terms of contract formed by conduct.&quot;">​</a></h1><p>(a) Except as otherwise provided in subsection (b) and subject to § <a href="http://law.lis.virginia.gov/vacode/59.1-503.1/" target="_blank" rel="noreferrer">59.1-503.1</a>, if a contract is formed by conduct of the parties, the terms of the contract are determined by consideration of the terms and conditions to which the parties expressly agreed, course of performance, course of dealing, usage of trade, the nature of the parties&#39; conduct, the records exchanged, the information or informational rights involved, and all other relevant circumstances. If a court cannot determine the terms of the contract from the foregoing factors, the supplementary principles of this chapter apply.</p><p>(b) This section does not apply if the parties authenticate a record of the contract or a party agrees, such as by manifesting assent, to the record containing the terms of the other party.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>.</p>',4),n=[c];function i(s,d,f,h,p,l){return t(),r("div",null,n)}const u=e(a,[["render",i]]);export{_ as __pageData,u as default};
