import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 59.1-502.5 Conditional offer or acceptance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-502.5.md","filePath":"vacode/59.1-502.5.md"}'),o={name:"vacode/59.1-502.5.md"},n=r('<h1 id="§-59-1-502-5-conditional-offer-or-acceptance" tabindex="-1">§ 59.1-502.5 Conditional offer or acceptance. <a class="header-anchor" href="#§-59-1-502-5-conditional-offer-or-acceptance" aria-label="Permalink to &quot;§ 59.1-502.5 Conditional offer or acceptance.&quot;">​</a></h1><p>(a) In this section, an offer or acceptance is conditional if it is conditioned on agreement by the other party to all the terms of the offer or acceptance.</p><p>(b) Except as otherwise provided in subsection (c), a conditional offer or acceptance precludes formation of a contract unless the other party agrees to its terms, such as by manifesting assent.</p><p>(c) If an offer and acceptance are in standard forms and at least one form is conditional, the following rules apply:</p><p>(1) Conditional language in a standard term precludes formation of a contract only if the actions of the party proposing the form are consistent with the conditional language, such as by refusing to perform, refusing to permit performance, or refusing to accept the benefits of the agreement, until its proposed terms are accepted.</p><p>(2) A party that agrees, such as by manifesting assent, to a conditional offer that is effective under paragraph (1) adopts the terms of the offer under § <a href="http://law.lis.virginia.gov/vacode/59.1-502.8/" target="_blank" rel="noreferrer">59.1-502.8</a> or § <a href="http://law.lis.virginia.gov/vacode/59.1-502.9/" target="_blank" rel="noreferrer">59.1-502.9</a>, except a term that conflicts with an expressly agreed term regarding price or quantity.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>.</p>',7),i=[n];function c(s,f,p,l,d,h){return t(),a("div",null,i)}const m=e(o,[["render",c]]);export{_ as __pageData,m as default};
