import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 59.1-502.7 Formation; releases of informational rights.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-502.7.md","filePath":"vacode/59.1-502.7.md"}'),i={name:"vacode/59.1-502.7.md"},o=t('<h1 id="§-59-1-502-7-formation-releases-of-informational-rights" tabindex="-1">§ 59.1-502.7 Formation; releases of informational rights. <a class="header-anchor" href="#§-59-1-502-7-formation-releases-of-informational-rights" aria-label="Permalink to &quot;§ 59.1-502.7 Formation; releases of informational rights.&quot;">​</a></h1><p>(a) A release is effective without consideration if it is:</p><p>(1) in a record to which the releasing party agrees, such as by manifesting assent, and which identifies the informational rights released; or</p><p>(2) enforceable under estoppel, implied license, or other law.</p><p>(b) A release continues for the duration of the informational rights released if the release does not specify its duration and does not require affirmative performance after the grant of the release by:</p><p>(1) the party granting the release; or</p><p>(2) the party receiving the release, except for relatively insignificant acts.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',8),n=[o];function s(l,f,c,h,p,_){return a(),r("div",null,n)}const m=e(i,[["render",s]]);export{g as __pageData,m as default};
