import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const d=JSON.parse(`{"title":"§ 59.1-503.9 Agreement for performance to party's satisfaction.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-503.9.md","filePath":"vacode/59.1-503.9.md"}`),o={name:"vacode/59.1-503.9.md"},s=r('<h1 id="§-59-1-503-9-agreement-for-performance-to-party-s-satisfaction" tabindex="-1">§ 59.1-503.9 Agreement for performance to party&#39;s satisfaction. <a class="header-anchor" href="#§-59-1-503-9-agreement-for-performance-to-party-s-satisfaction" aria-label="Permalink to &quot;§ 59.1-503.9 Agreement for performance to party&#39;s satisfaction.&quot;">​</a></h1><p>(a) Except as otherwise provided in subsection (b), an agreement that provides that the performance of one party is to be to the satisfaction or approval of the other party requires performance sufficient to satisfy a reasonable person in the position of the party that must be satisfied.</p><p>(b) Performance must be to the subjective satisfaction of the other party if:</p><p>(1) the agreement expressly so provides, such as by stating that approval is in the &quot;sole discretion&quot; of the party, or words of similar import; or</p><p>(2) the agreement is for informational content to be evaluated in reference to subjective characteristics such as aesthetics, appeal, suitability to taste, or subjective quality.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>.</p>',6),i=[s];function n(c,p,f,h,l,_){return t(),a("div",null,i)}const u=e(o,[["render",n]]);export{d as __pageData,u as default};
