import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 59.1-505.2 Title to copy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-505.2.md","filePath":"vacode/59.1-505.2.md"}'),a={name:"vacode/59.1-505.2.md"},r=i('<h1 id="§-59-1-505-2-title-to-copy" tabindex="-1">§ 59.1-505.2 Title to copy. <a class="header-anchor" href="#§-59-1-505-2-title-to-copy" aria-label="Permalink to &quot;§ 59.1-505.2 Title to copy.&quot;">​</a></h1><p>(a) In a license:</p><p>(1) title to a copy is determined by the license;</p><p>(2) a licensee&#39;s right under the license to possession or control of a copy is governed by the license and does not depend solely on title to the copy; and</p><p>(3) if a licensor reserves title to a copy, the licensor retains title to that copy and any copies made of it, unless the license grants the licensee a right to make and sell copies to others, in which case the reservation of title applies only to copies delivered to the licensee by the licensor.</p><p>(b) If an agreement provides for transfer of title to a copy, title passes:</p><p>(1) at the time and place specified in the agreement; or</p><p>(2) if the agreement does not specify a time and place:</p><p>(A) with respect to delivery of a copy on a tangible medium, at the time and place the licensor completed its obligations with respect to tender of the copy; or</p><p>(B) with respect to electronic delivery of a copy, if a first sale occurs under federal copyright law, at the time and place at which the licensor completed its obligations with respect to tender of the copy.</p><p>(c) If the party to which title passes under the contract refuses delivery of the copy or rejects the terms of the agreement, title revests in the licensor.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>.</p>',12),s=[r];function c(n,p,l,h,d,f){return t(),o("div",null,s)}const m=e(a,[["render",c]]);export{y as __pageData,m as default};
