import{_ as t,o as e,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 59.1-507.10 Retraction of anticipatory repudiation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-507.10.md","filePath":"vacode/59.1-507.10.md"}'),i={name:"vacode/59.1-507.10.md"},n=r('<h1 id="§-59-1-507-10-retraction-of-anticipatory-repudiation" tabindex="-1">§ 59.1-507.10 Retraction of anticipatory repudiation. <a class="header-anchor" href="#§-59-1-507-10-retraction-of-anticipatory-repudiation" aria-label="Permalink to &quot;§ 59.1-507.10 Retraction of anticipatory repudiation.&quot;">​</a></h1><p>(a) A repudiating party may retract its repudiation until its next performance is due unless the aggrieved party, after the repudiation, has canceled the contract, materially changed its position, or otherwise indicated that it considers the repudiation final.</p><p>(b) A retraction may be by any method that clearly indicates to the aggrieved party that the repudiating party intends to perform the contract. However, a retraction must contain any assurance justifiably demanded under § <a href="http://law.lis.virginia.gov/vacode/59.1-507.8/" target="_blank" rel="noreferrer">59.1-507.8</a>.</p><p>(c) Retraction restores a repudiating party&#39;s rights under the contract with due excuse and allowance to the aggrieved party for any delay caused by the repudiation.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>.</p>',5),o=[n];function c(d,p,s,l,h,_){return e(),a("div",null,o)}const g=t(i,[["render",c]]);export{f as __pageData,g as default};
