import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 59.1-508.1 Remedies in general.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-508.1.md","filePath":"vacode/59.1-508.1.md"}'),o={name:"vacode/59.1-508.1.md"},i=a('<h1 id="§-59-1-508-1-remedies-in-general" tabindex="-1">§ 59.1-508.1 Remedies in general. <a class="header-anchor" href="#§-59-1-508-1-remedies-in-general" aria-label="Permalink to &quot;§ 59.1-508.1 Remedies in general.&quot;">​</a></h1><p>(a) The remedies provided in this chapter are cumulative, but a party may not recover more than once for the same loss.</p><p>(b) Except as otherwise provided in §§ <a href="http://law.lis.virginia.gov/vacode/59.1-508.3/" target="_blank" rel="noreferrer">59.1-508.3</a> and <a href="http://law.lis.virginia.gov/vacode/59.1-508.4/" target="_blank" rel="noreferrer">59.1-508.4</a>, if a party is in breach of contract, whether or not the breach is material, the aggrieved party has the remedies provided in the agreement or this chapter, but the aggrieved party shall continue to comply with any contractual use terms with respect to information or copies received from the other party, but the contractual use terms do not apply to information or copies properly received or obtained from another source.</p><p>(c) Rescission or a claim for rescission of the contract, or refusal of the information, does not preclude and is not inconsistent with a claim for damages or other remedy.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>.</p>',5),n=[i];function s(c,l,h,d,p,m){return r(),t("div",null,n)}const g=e(o,[["render",s]]);export{f as __pageData,g as default};
