import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 59.1-508.3 Contractual modification of remedy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-508.3.md","filePath":"vacode/59.1-508.3.md"}'),r={name:"vacode/59.1-508.3.md"},o=t('<h1 id="§-59-1-508-3-contractual-modification-of-remedy" tabindex="-1">§ 59.1-508.3 Contractual modification of remedy. <a class="header-anchor" href="#§-59-1-508-3-contractual-modification-of-remedy" aria-label="Permalink to &quot;§ 59.1-508.3 Contractual modification of remedy.&quot;">​</a></h1><p>(a) Except as otherwise provided in this section and in § <a href="http://law.lis.virginia.gov/vacode/59.1-508.4/" target="_blank" rel="noreferrer">59.1-508.4</a>:</p><p>(1) an agreement may provide for remedies in addition to or in substitution for those provided in this chapter and may limit or alter the measure of damages recoverable under this chapter or a party&#39;s other remedies under this chapter, such as by precluding a party&#39;s right to cancel for breach of contract, limiting remedies to returning or delivering copies and repayment of the contract fee, or limiting remedies to repair or replacement of the nonconforming copies; and</p><p>(2) resort to a contractual remedy is optional unless the remedy is expressly agreed to be exclusive, in which case it is the sole remedy.</p><p>(b) Subject to subsection (c), if performance of an exclusive or limited remedy causes the remedy to fail of its essential purpose, the aggrieved party may pursue other remedies under this chapter.</p><p>(c) Failure or unconscionability of an agreed exclusive or limited remedy makes a term disclaiming or limiting consequential or incidental damages unenforceable unless the agreement expressly makes the disclaimer or limitation independent of the agreed remedy.</p><p>(d) Consequential damages and incidental damages may be excluded or limited by agreement unless the exclusion or limitation is unconscionable. Exclusion or limitation of consequential damages for personal injury in a consumer contract for a computer program that is subject to this chapter and is contained in consumer goods is prima facie unconscionable, but exclusion or limitation of damages for a commercial loss is not unconscionable.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>.</p>',8),n=[o];function s(c,d,l,m,p,u){return a(),i("div",null,n)}const g=e(r,[["render",s]]);export{h as __pageData,g as default};
