import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse(`{"title":"§ 59.1-508.9 Licensee's damages.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-508.9.md","filePath":"vacode/59.1-508.9.md"}`),r={name:"vacode/59.1-508.9.md"},n=o('<h1 id="§-59-1-508-9-licensee-s-damages" tabindex="-1">§ 59.1-508.9 Licensee&#39;s damages. <a class="header-anchor" href="#§-59-1-508-9-licensee-s-damages" aria-label="Permalink to &quot;§ 59.1-508.9 Licensee&#39;s damages.&quot;">​</a></h1><p>(a) Subject to subsection (b) and except as otherwise provided in § <a href="http://law.lis.virginia.gov/vacode/59.1-508.7/" target="_blank" rel="noreferrer">59.1-508.7</a>, a breach of contract by a licensor entitles the licensee to recover the following compensation for losses resulting in the ordinary course from the breach or, if appropriate, as to the whole contract, less expenses avoided as a result of the breach to the extent not otherwise accounted for under this section:</p><p>(1) damages measured in any combination of the following ways, but not to exceed the market value of the performance that was the subject of the breach plus restitution of any amounts paid for performance not received and not accounted for within the indicated recovery:</p><p>(A) with respect to performance that has been accepted and the acceptance not rightfully revoked, the value of the performance required less the value of the performance accepted as of the time and place of acceptance;</p><p>(B) with respect to performance that has not been rendered or that was rightfully refused or acceptance of which was rightfully revoked:</p><p>(i) the amount of any payments made and the value of other consideration given to the licensor with respect to that performance and not previously returned to the licensee;</p><p>(ii) the market value of the performance less the contract fee for that performance; or</p><p>(iii) the cost of a commercially reasonable substitute transaction less the contract fee under the breached contract, if the substitute transaction was entered into by the licensee in good faith and without unreasonable delay for substantially similar information with the same contractual use terms; or</p><p>(C) damages calculated in any reasonable manner; and</p><p>(2) incidental and consequential damages.</p><p>(b) The amount of damages must be reduced by any unpaid contract fees for performance by the licensor which has been accepted by the licensee and as to which the acceptance has not been rightfully revoked.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>.</p>',12),c=[n];function s(i,h,l,d,p,f){return t(),a("div",null,c)}const b=e(r,[["render",s]]);export{m as __pageData,b as default};
