import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 59.1-513 Transmission of caller identification information required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-513.md","filePath":"vacode/59.1-513.md"}'),a={name:"vacode/59.1-513.md"},o=n('<h1 id="§-59-1-513-transmission-of-caller-identification-information-required" tabindex="-1">§ 59.1-513 Transmission of caller identification information required. <a class="header-anchor" href="#§-59-1-513-transmission-of-caller-identification-information-required" aria-label="Permalink to &quot;§ 59.1-513 Transmission of caller identification information required.&quot;">​</a></h1><p>A. A telephone solicitor who makes a telephone solicitation call shall transmit the telephone number, and, when available by the telephone solicitor&#39;s carrier, the name of the telephone solicitor. The number so provided must permit, during regular business hours, any individual to make a request not to receive telephone solicitation calls.</p><p>B. No telephone solicitor shall take any intentional action to prevent the transmission of the telephone solicitor&#39;s name or telephone number to any person receiving a telephone solicitation call or engage in any conduct that results in the display of false or misleading caller identification information on the called person&#39;s telephone.</p><p>C. It shall not be a violation of this section to substitute for the name and telephone number used in, or billed for, making the call the name of the person on whose behalf the telephone solicitation call is being made and that person&#39;s customer service telephone number.</p><p>2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0528" target="_blank" rel="noreferrer">528</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0553" target="_blank" rel="noreferrer">553</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0202" target="_blank" rel="noreferrer">202</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0224" target="_blank" rel="noreferrer">224</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0263" target="_blank" rel="noreferrer">263</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0607" target="_blank" rel="noreferrer">607</a>.</p>',5),r=[o];function l(s,c,h,f,p,d){return i(),t("div",null,r)}const u=e(a,[["render",l]]);export{g as __pageData,u as default};
