import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 59.1-515 Individual action for damages.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-515.md","filePath":"vacode/59.1-515.md"}'),o={name:"vacode/59.1-515.md"},n=i('<h1 id="§-59-1-515-individual-action-for-damages" tabindex="-1">§ 59.1-515 Individual action for damages. <a class="header-anchor" href="#§-59-1-515-individual-action-for-damages" aria-label="Permalink to &quot;§ 59.1-515 Individual action for damages.&quot;">​</a></h1><p>A. Any natural person who is aggrieved by a violation of this chapter shall be entitled to initiate an action against any responsible person to enjoin such violation and to recover from any responsible person damages in the amount of $500 for a first violation, $1,000 for a second violation, and $5,000 for each subsequent violation.</p><p>B. If the court finds a willful violation, the court may, in its discretion, increase the amount of any damages awarded for a first or second violation under subsection A to an amount not exceeding $5,000.</p><p>C. Notwithstanding any other provision of law to the contrary, in addition to any damages awarded, such person may be awarded under subsection A or B reasonable attorney fees and court costs.</p><p>D. An action for damages, attorney fees, and costs brought under this section may be filed in an appropriate general district court or small claims court against any responsible person so long as the amount claimed does not exceed the jurisdictional limits set forth in § <a href="/vacode/16.1-77/">16.1-77</a> or <a href="/vacode/16.1-122.2/">16.1-122.2</a>, as applicable. Any action brought under this section that includes a request for an injunction shall be filed in an appropriate circuit court.</p><p>2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0528" target="_blank" rel="noreferrer">528</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0553" target="_blank" rel="noreferrer">553</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0264" target="_blank" rel="noreferrer">264</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0263" target="_blank" rel="noreferrer">263</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0607" target="_blank" rel="noreferrer">607</a>.</p>',6),r=[n];function s(l,c,d,f,p,g){return a(),t("div",null,r)}const _=e(o,[["render",s]]);export{u as __pageData,_ as default};
