import{_ as t,o as e,c as a,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 59.1-528 Complaint investigations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-528.md","filePath":"vacode/59.1-528.md"}'),o={name:"vacode/59.1-528.md"},i=n('<h1 id="§-59-1-528-complaint-investigations" tabindex="-1">§ 59.1-528 Complaint investigations. <a class="header-anchor" href="#§-59-1-528-complaint-investigations" aria-label="Permalink to &quot;§ 59.1-528 Complaint investigations.&quot;">​</a></h1><p>In the event that the Attorney General, any attorney for the Commonwealth, or the attorney for any county, city, or town investigates a complaint for a violation of § <a href="http://law.lis.virginia.gov/vacode/59.1-527/" target="_blank" rel="noreferrer">59.1-527</a> and determines that the supplier has not violated the section, and if the supplier requests, the Attorney General, any attorney for the Commonwealth, or the attorney for any county, city, or town shall promptly issue a signed statement indicating that a violation of § <a href="http://law.lis.virginia.gov/vacode/59.1-527/" target="_blank" rel="noreferrer">59.1-527</a> has not been found. Subject to the disclosures allowed by this section, it shall be the duty of the Attorney General, the attorney for the Commonwealth, or the attorney for any city, county or town, or their designees, that investigates any complaint for violation of § <a href="http://law.lis.virginia.gov/vacode/59.1-527/" target="_blank" rel="noreferrer">59.1-527</a> to maintain the confidentiality of all evidence, testimony, documents, or other results of such investigations, including the names of the complainant, and the individual, corporation or other entity that is the subject of the investigation. Nothing herein contained shall be construed to prevent the presentation and disclosure of any such investigative evidence in an action or proceeding brought under this chapter.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0798" target="_blank" rel="noreferrer">798</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0817" target="_blank" rel="noreferrer">817</a>.</p>',3),r=[i];function s(l,h,c,d,f,p){return e(),a("div",null,r)}const _=t(o,[["render",s]]);export{g as __pageData,_ as default};
