import{_ as t,o as e,c as n,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 59.1-536 Complaint investigations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-536.md","filePath":"vacode/59.1-536.md"}'),o={name:"vacode/59.1-536.md"},i=a('<h1 id="§-59-1-536-complaint-investigations" tabindex="-1">§ 59.1-536 Complaint investigations. <a class="header-anchor" href="#§-59-1-536-complaint-investigations" aria-label="Permalink to &quot;§ 59.1-536 Complaint investigations.&quot;">​</a></h1><p>In the event that the Attorney General, any attorney for the Commonwealth, or the attorney for any county, city, or town investigates a complaint for a violation of § <a href="http://law.lis.virginia.gov/vacode/59.1-535/" target="_blank" rel="noreferrer">59.1-535</a> and determines that the person has not violated the section, and if the person requests, the Attorney General, any attorney for the Commonwealth, or the attorney for any county, city, or town shall promptly issue a signed statement indicating that a violation of § <a href="http://law.lis.virginia.gov/vacode/59.1-535/" target="_blank" rel="noreferrer">59.1-535</a> has not been found. Subject to the disclosures allowed by this section, it shall be the duty of the Attorney General, the attorney for the Commonwealth, or the attorney for any county, city, or town, or their designees, that investigates any complaint for violation of § <a href="http://law.lis.virginia.gov/vacode/59.1-535/" target="_blank" rel="noreferrer">59.1-535</a> to maintain the confidentiality of all evidence, testimony, documents, or other results of such investigations, including the names of the complainant, and the person that is the subject of the investigation. Nothing herein contained shall be construed to prevent the presentation and disclosure of any such investigative evidence in an action or proceeding brought under this chapter.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0861" target="_blank" rel="noreferrer">861</a>.</p>',3),r=[i];function s(h,l,c,d,f,p){return e(),n("div",null,r)}const g=t(o,[["render",s]]);export{_ as __pageData,g as default};
