import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 59.1-569 Fantasy contests conducted under this chapter not illegal gambling.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-569.md","filePath":"vacode/59.1-569.md"}'),r={name:"vacode/59.1-569.md"},o=n('<h1 id="§-59-1-569-fantasy-contests-conducted-under-this-chapter-not-illegal-gambling" tabindex="-1">§ 59.1-569 Fantasy contests conducted under this chapter not illegal gambling. <a class="header-anchor" href="#§-59-1-569-fantasy-contests-conducted-under-this-chapter-not-illegal-gambling" aria-label="Permalink to &quot;§ 59.1-569 Fantasy contests conducted under this chapter not illegal gambling.&quot;">​</a></h1><p>A. Nothing contained in Article 1 (§ <a href="/vacode/18.2-325/">18.2-325</a> et seq.) of Chapter 8 of Title 18.2 shall be applicable to a fantasy contest conducted in accordance with this chapter. The award of any prize money for any fantasy contest shall not be deemed to be part of any gaming contract within the purview of § <a href="/vacode/11-14/">11-14</a>.</p><p>B. This section shall not apply to any sports betting or related activity that is lawful under Article 2 (§ <a href="/vacode/58.1-4030/">58.1-4030</a> et seq.) of Chapter 40 of Title 58.1, which shall be regulated pursuant to such chapter.</p><p>2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0318" target="_blank" rel="noreferrer">318</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0703" target="_blank" rel="noreferrer">703</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1218" target="_blank" rel="noreferrer">1218</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1256" target="_blank" rel="noreferrer">1256</a>.</p>',4),i=[o];function c(l,s,h,d,p,g){return t(),a("div",null,i)}const u=e(r,[["render",c]]);export{_ as __pageData,u as default};
