import{_ as e,o as t,c as s,O as r}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 59.1-69 Certificate required of person transacting business under assumed name.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-69.md","filePath":"vacode/59.1-69.md"}'),a={name:"vacode/59.1-69.md"},n=r('<h1 id="§-59-1-69-certificate-required-of-person-transacting-business-under-assumed-name" tabindex="-1">§ 59.1-69 Certificate required of person transacting business under assumed name. <a class="header-anchor" href="#§-59-1-69-certificate-required-of-person-transacting-business-under-assumed-name" aria-label="Permalink to &quot;§ 59.1-69 Certificate required of person transacting business under assumed name.&quot;">​</a></h1><p>A. As used in this chapter, unless the context requires a different meaning:</p><p>&quot;Commission&quot; means the State Corporation Commission.</p><p>&quot;Person&quot; has the meaning prescribed in § <a href="/vacode/1-230/">1-230</a>.</p><p>B. No person shall conduct or transact business in the Commonwealth under any assumed or fictitious name unless such person files in the office of the clerk of the Commission a certificate of assumed or fictitious name.</p><p>C. No person shall use an assumed or fictitious name in the conduct of the person&#39;s business to intentionally misrepresent the geographic origin or location of the person.</p><p>Code 1950, § 59-169; 1968, c. 439; 1987, c. 702; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0168" target="_blank" rel="noreferrer">168</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0904" target="_blank" rel="noreferrer">904</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0594" target="_blank" rel="noreferrer">594</a>.</p>',7),i=[n];function o(c,u,d,p,f,l){return t(),s("div",null,i)}const _=e(a,[["render",o]]);export{h as __pageData,_ as default};
