import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 59.1-9.14 Limitation of actions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/59.1-9.14.md","filePath":"vacode/59.1-9.14.md"}'),r={name:"vacode/59.1-9.14.md"},i=o('<h1 id="§-59-1-9-14-limitation-of-actions" tabindex="-1">§ 59.1-9.14 Limitation of actions. <a class="header-anchor" href="#§-59-1-9-14-limitation-of-actions" aria-label="Permalink to &quot;§ 59.1-9.14 Limitation of actions.&quot;">​</a></h1><p>(a) An action under § <a href="http://law.lis.virginia.gov/vacode/59.1-9.15/" target="_blank" rel="noreferrer">59.1-9.15</a> (a) to recover a civil penalty is barred if it is not commenced within four years after the cause of action accrues.</p><p>(b) An action under § <a href="http://law.lis.virginia.gov/vacode/59.1-9.12/" target="_blank" rel="noreferrer">59.1-9.12</a> (b) or § <a href="http://law.lis.virginia.gov/vacode/59.1-9.15/" target="_blank" rel="noreferrer">59.1-9.15</a> (b) to recover damages is barred if it is not commenced within four years after the cause of action accrues, or within one year after the conclusion of any action or proceeding under § <a href="http://law.lis.virginia.gov/vacode/59.1-9.15/" target="_blank" rel="noreferrer">59.1-9.15</a> (a) commenced within or before that time based in whole or in part on any matter complained of in the action for damages, whichever is later.</p><p>1974, c. 545.</p>',4),n=[i];function c(s,l,d,f,_,h){return a(),t("div",null,n)}const v=e(r,[["render",c]]);export{p as __pageData,v as default};
