import{_ as e,o as t,c as s,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 6.2-1005 Deposit or other use of trust funds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1005.md","filePath":"vacode/6.2-1005.md"}'),o={name:"vacode/6.2-1005.md"},i=r('<h1 id="§-6-2-1005-deposit-or-other-use-of-trust-funds" tabindex="-1">§ 6.2-1005 Deposit or other use of trust funds. <a class="header-anchor" href="#§-6-2-1005-deposit-or-other-use-of-trust-funds" aria-label="Permalink to &quot;§ 6.2-1005 Deposit or other use of trust funds.&quot;">​</a></h1><p>A. Funds received or held in the trust department of a bank or by a trust company awaiting investment or distribution shall not be used by the bank or trust company in the conduct of its business.</p><p>B. Notwithstanding subsection A, such funds may be deposited by a bank in its commercial or savings department to the credit of its trust department, if the bank first delivers to the trust department, as collateral security therefor, securities of any of the following classes:</p><ol><li><p>Bonds, notes, or certificates of indebtedness of the United States;</p></li><li><p>Other readily marketable securities of the classes in which fiduciaries are authorized or permitted to invest trust funds, as set forth in § <a href="http://law.lis.virginia.gov/vacode/64.2-1502/" target="_blank" rel="noreferrer">64.2-1502</a>; or</p></li><li><p>Other readily marketable bonds, notes, or debentures, commonly known as investment securities, meeting the following requirements:</p></li></ol><p>a. That the issue be of a sufficiently large total to make marketability possible;</p><p>b. Such a public distribution of the securities must have been provided for or made in a manner to protect or insure the marketability of the issue; and</p><p>c. That the trust agreement under which the security is issued provides for a trustee independent of the obligor, which trustee must be a trust institution.</p><p>C. The securities deposited as collateral pursuant to subsection B shall be owned by the bank and shall at all times be at least equal in market value to the amount of trust funds so used in the conduct of the business of the bank less such amount thereof as shall be insured by the Federal Deposit Insurance Corporation under existing or future federal law.</p><p>D. In the event of the failure or liquidation of such bank, the owners of the funds held in trust for investment shall have a lien on the bonds or other securities so set apart in addition to their claim against the estate of the bank.</p><p>Code 1950, § 6-99; 1966, c. 584, § 6.1-21; 1992, c. 810; 1993, c. 432; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0007" target="_blank" rel="noreferrer">7</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',10),a=[i];function n(u,l,d,h,c,f){return t(),s("div",null,a)}const m=e(o,[["render",n]]);export{b as __pageData,m as default};
