import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 6.2-1033 Fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1033.md","filePath":"vacode/6.2-1033.md"}'),o={name:"vacode/6.2-1033.md"},r=t('<h1 id="§-6-2-1033-fees" tabindex="-1">§ 6.2-1033 Fees. <a class="header-anchor" href="#§-6-2-1033-fees" aria-label="Permalink to &quot;§ 6.2-1033 Fees.&quot;">​</a></h1><p>A. In order to defray the costs of their examination, supervision, and regulation, every trust company shall pay a fee of $330 per day per examiner during examinations.</p><p>B. Each trust company and each trust company holding company shall also pay to the Commission:</p><ol><li><p>Such additional or special costs as the Commission may incur in connection with its examination;</p></li><li><p>For investigating an application for authority to establish a branch office pursuant to § <a href="http://law.lis.virginia.gov/vacode/6.2-1028/" target="_blank" rel="noreferrer">6.2-1028</a>, a fee of $1,800;</p></li><li><p>For investigating an application to change the location of a principal office or branch office, a fee of $1,000; and</p></li><li><p>For investigating an application made pursuant to § <a href="http://law.lis.virginia.gov/vacode/6.2-1023/" target="_blank" rel="noreferrer">6.2-1023</a>, a fee of $7,000.</p></li></ol><p>1993, c. 432, § 6.1-32.25; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0006" target="_blank" rel="noreferrer">6</a>; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0140" target="_blank" rel="noreferrer">140</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',5),n=[r];function l(s,c,p,f,h,_){return a(),i("div",null,n)}const m=e(o,[["render",l]]);export{g as __pageData,m as default};
