import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-1038 Appointment of receiver.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1038.md","filePath":"vacode/6.2-1038.md"}'),r={name:"vacode/6.2-1038.md"},n=a('<h1 id="§-6-2-1038-appointment-of-receiver" tabindex="-1">§ 6.2-1038 Appointment of receiver. <a class="header-anchor" href="#§-6-2-1038-appointment-of-receiver" aria-label="Permalink to &quot;§ 6.2-1038 Appointment of receiver.&quot;">​</a></h1><p>A. When in the judgment of the Commission it is necessary for the protection of the interests of the Commonwealth or of the creditors of any trust company doing business in the Commonwealth, the Commission shall apply to any court in the Commonwealth having jurisdiction to appoint receivers for the appointment of a receiver to take charge of the business affairs and assets and to wind up the affairs and business of any such trust company failing to comply with the requirements of the Commission, or found upon examination to be insolvent or unable to meet its obligations and the legal demands made upon it in the ordinary course and conduct of its business.</p><p>B. Reference is hereby made to §§ <a href="http://law.lis.virginia.gov/vacode/6.2-916/" target="_blank" rel="noreferrer">6.2-916</a> through <a href="http://law.lis.virginia.gov/vacode/6.2-924/" target="_blank" rel="noreferrer">6.2-924</a> and Article 14 (§ <a href="http://law.lis.virginia.gov/vacode/6.2-925/" target="_blank" rel="noreferrer">6.2-925</a> et seq.) of Chapter 8 for provisions applicable to receiverships of trust companies.</p><p>Code 1950, § 6-116; 1966, c. 584, § 6.1-102; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),i=[n];function s(c,p,h,l,f,d){return t(),o("div",null,i)}const u=e(r,[["render",s]]);export{_ as __pageData,u as default};
