import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 6.2-1054 Certificate required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1054.md","filePath":"vacode/6.2-1054.md"}'),r={name:"vacode/6.2-1054.md"},s=a('<h1 id="§-6-2-1054-certificate-required" tabindex="-1">§ 6.2-1054 Certificate required. <a class="header-anchor" href="#§-6-2-1054-certificate-required" aria-label="Permalink to &quot;§ 6.2-1054 Certificate required.&quot;">​</a></h1><p>No trust subsidiary, other than a wholly owned subsidiary of a national banking association, shall engage in trust business without first obtaining a certificate of authority from the Commission, or the Comptroller of the Currency if it is organized as a national banking association. The Commission shall not grant such certificate unless:</p><ol><li><p>The capital and surplus of the trust subsidiary equal or exceed $200,000; and</p></li><li><p>The Commission is satisfied that (i) the trust subsidiary is capable of complying with the provisions of this chapter and (ii) the officers and directors have the moral fitness and business qualifications necessary to manage the trust subsidiary.</p></li></ol><p>1974, c. 286, § 6.1-32.5; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0801" target="_blank" rel="noreferrer">801</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),o=[s];function n(c,l,h,d,f,u){return i(),t("div",null,o)}const m=e(r,[["render",n]]);export{p as __pageData,m as default};
