import{_ as t,o as e,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 6.2-1066 Interstate trust offices by Virginia state banks.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1066.md","filePath":"vacode/6.2-1066.md"}'),s={name:"vacode/6.2-1066.md"},r=i('<h1 id="§-6-2-1066-interstate-trust-offices-by-virginia-state-banks" tabindex="-1">§ 6.2-1066 Interstate trust offices by Virginia state banks. <a class="header-anchor" href="#§-6-2-1066-interstate-trust-offices-by-virginia-state-banks" aria-label="Permalink to &quot;§ 6.2-1066 Interstate trust offices by Virginia state banks.&quot;">​</a></h1><p>A. With the prior approval of the Commission, any Virginia state bank or state trust company may establish a new trust office or acquire a trust office in a state other than the Commonwealth.</p><p>B. A Virginia state bank or state trust company desiring to establish and maintain a trust office in another state under this section shall file an application or notice on a form prescribed by the Commission and pay the branch application fee set forth in subdivision B 3 of § <a href="http://law.lis.virginia.gov/vacode/6.2-908/" target="_blank" rel="noreferrer">6.2-908</a>. If the Commission finds that the applicant has the financial resources sufficient to undertake the proposed expansion without adversely affecting its soundness and that the laws of the host state permit the establishment of the trust office, it may approve the application. In acting on the application, the Commission shall consider the views of the state bank supervisor of the host state where the trust office is proposed to be located.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>, § 6.1-32.33; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),o=[r];function n(c,h,f,p,l,d){return e(),a("div",null,o)}const b=t(s,[["render",n]]);export{u as __pageData,b as default};
