import{_ as t,o as e,c as i,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 6.2-1068 Establishing or acquiring an interstate trust office; additional trust offices; notice of closure.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1068.md","filePath":"vacode/6.2-1068.md"}'),o={name:"vacode/6.2-1068.md"},n=a('<h1 id="§-6-2-1068-establishing-or-acquiring-an-interstate-trust-office-additional-trust-offices-notice-of-closure" tabindex="-1">§ 6.2-1068 Establishing or acquiring an interstate trust office; additional trust offices; notice of closure. <a class="header-anchor" href="#§-6-2-1068-establishing-or-acquiring-an-interstate-trust-office-additional-trust-offices-notice-of-closure" aria-label="Permalink to &quot;§ 6.2-1068 Establishing or acquiring an interstate trust office; additional trust offices; notice of closure.&quot;">​</a></h1><p>A. An out-of-state trust institution that does not already maintain a trust office in the Commonwealth and that meets the requirements of this article may:</p><ol><li><p>Establish and maintain a new trust office in the Commonwealth; or</p></li><li><p>Acquire and maintain a trust office in the Commonwealth.</p></li></ol><p>B. An out-of-state trust institution that maintains a trust office in the Commonwealth under this article may establish or acquire additional trust offices in the Commonwealth to the same extent that a state trust institution may establish or acquire additional offices in the Commonwealth, provided it follows the procedures for establishing or acquiring such offices set forth in this article.</p><p>C. An out-of-state trust institution that maintains an office in the Commonwealth under this article shall give at least 30 days&#39; prior written notice, or in the case of an emergency transaction, such shorter notice as is consistent with applicable state or federal law, to the Commission of any merger, consolidation, or other transaction involving the trust institution that would cause any trust office operated by the institution in this state to be maintained by another trust institution or cause the operation of such an office to cease.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>, §§ 6.1-32.36, 6.1-32.37, 6.1-32.42, 6.1-32.44; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',6),s=[n];function r(c,l,f,h,u,d){return e(),i("div",null,s)}const _=t(o,[["render",r]]);export{p as __pageData,_ as default};
