import{_ as t,o as e,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-1070 Conditions for approval.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1070.md","filePath":"vacode/6.2-1070.md"}'),i={name:"vacode/6.2-1070.md"},s=o('<h1 id="§-6-2-1070-conditions-for-approval" tabindex="-1">§ 6.2-1070 Conditions for approval. <a class="header-anchor" href="#§-6-2-1070-conditions-for-approval" aria-label="Permalink to &quot;§ 6.2-1070 Conditions for approval.&quot;">​</a></h1><p>A trust office of an out-of-state trust institution shall not be acquired or established under this article unless:</p><ol><li><p>In the case of a new trust office, the laws of the home state of the out-of-state trust institution permit state trust institutions to establish and maintain new trust offices in that state under substantially the same terms as set forth in this article.</p></li><li><p>In the case of a trust office to be established through the acquisition of a trust office, the laws of the home state of the out-of-state trust institution permit state trust institutions to establish and maintain trust offices in that state through the acquisition of trust offices under substantially the same terms as set forth in this article.</p></li></ol><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>, § 6.1-32.39; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),r=[s];function n(l,f,c,h,u,p){return e(),a("div",null,r)}const m=t(i,[["render",n]]);export{_ as __pageData,m as default};
