import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-1085 When security not required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1085.md","filePath":"vacode/6.2-1085.md"}'),a={name:"vacode/6.2-1085.md"},o=i('<h1 id="§-6-2-1085-when-security-not-required" tabindex="-1">§ 6.2-1085 When security not required. <a class="header-anchor" href="#§-6-2-1085-when-security-not-required" aria-label="Permalink to &quot;§ 6.2-1085 When security not required.&quot;">​</a></h1><p>No association with a minimum combined unimpaired capital and surplus of $50,000 or more shall be required by any officer or court of the Commonwealth to give security upon appointment to or acceptance of any fiduciary office which it may, by law, be authorized to execute, or to give security upon any bond given pursuant to § <a href="http://law.lis.virginia.gov/vacode/19.2-386.6/" target="_blank" rel="noreferrer">19.2-386.6</a> or similar statute. No association shall qualify on an estate having a value in excess of its combined unimpaired capital and surplus without giving security for such excess on its bond, unless the giving of such security is waived under the terms of the governing instrument or by court order.</p><p>1984, c. 303, § 6.1-195.82; 1993, c. 866; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>.</p>',3),n=[o];function s(c,u,l,d,h,p){return r(),t("div",null,n)}const g=e(a,[["render",s]]);export{_ as __pageData,g as default};
