import{_ as s,o,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 6.2-1096 Surrender of trust powers by association.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-1096.md","filePath":"vacode/6.2-1096.md"}'),a={name:"vacode/6.2-1096.md"},r=e("h1",{id:"§-6-2-1096-surrender-of-trust-powers-by-association",tabindex:"-1"},[t("§ 6.2-1096 Surrender of trust powers by association. "),e("a",{class:"header-anchor",href:"#§-6-2-1096-surrender-of-trust-powers-by-association","aria-label":'Permalink to "§ 6.2-1096 Surrender of trust powers by association."'},"​")],-1),n=e("p",null,"Any association that has been granted the right to exercise trust powers and that desires to surrender such rights shall file with the Commission a certified copy of the resolution of its board of directors signifying such desire. Upon receipt of such resolution, the Commission shall make an investigation. If the Commission is satisfied that the association has been properly discharged from all fiduciary duties that it has undertaken, the Commission shall issue a certificate to such association certifying that it is no longer authorized to exercise fiduciary powers. Upon issuance of such a certificate by the Commission, an association shall no longer be subject to the provisions of this article and shall not exercise thereafter any of the powers granted by this article without first applying for and obtaining a new authorization to exercise such powers.",-1),c=e("p",null,[t("1984, c. 303, § 6.1-195.93; 2010, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794",target:"_blank",rel:"noreferrer"},"794"),t(".")],-1),h=[r,n,c];function d(l,f,u,p,m,_){return o(),i("div",null,h)}const y=s(a,[["render",d]]);export{b as __pageData,y as default};
